import React,{Component, useState} from 'react';
import {useHistory} from 'react-router-dom';
import { blue, purple, pink } from "@material-ui/core/colors";
import {TextField,Button, ButtonGroup,Grid}from '@material-ui/core';
import {
    makeStyles,
    createMuiTheme,
    ThemeProvider
  } from "@material-ui/core/styles";



const SignUp=()=>
{
    const [name ,setName] = useState();
    const[email,setemail]=useState();
    const[password,setpassword]= useState();
    const [values, setValues] = useState({
        Name: '',
        email: '',
        password:'',
      });
      const handleChange = (event) => {
        const{name,value}=event.target;

        setValues({

          ...values,
          [name]: value,

        });
      }; 
      const handleSubmit= e=>{
        e.preventDefault();
      };

      const Submit=()=>{

        const user = localStorage.getItem("user")

        const userDetail = {
          name : values.Name,
          email:values.email,
          password:values.password
        }

        if(user=== null) {
            const a = []
            a.push(userDetail)
            localStorage.setItem('user', JSON.stringify(a))

        }else {
            const data = JSON.parse(user)
            if (data.some(o => o.email == values.email)) {
               alert('email already exists');
               return
            }else{

              data.push(userDetail)
              localStorage.setItem('user', JSON.stringify(data))
            }
            return null


        }
      }
      const uploadImage=async(e)=>{
        const files= e.target.files[0];
      const base64= await  convertBase64(files);
      console.log(base64);

    }
    const convertBase64=(file)=>{
        return new Promise((resolve,reject)=>{
            const fileReader = new FileReader();
            fileReader.readAsDataURL(file);

            fileReader.onload=(()=>{
                resolve (fileReader.result);

            });
            fileReader.onerror=((error)=>{
                reject(error);
            })
        })
    }
    const useStyles = makeStyles((theme) => ({

        root: {
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            padding: theme.spacing(2),

            '& .MuiTextField-root': {
              margin: theme.spacing(1),
              width: '300px',
            },
            '& .MuiButtonBase-root': {
              margin: theme.spacing(2),
            },
          },
        }));
      const defaultTheme = createMuiTheme({
        palette: {
          primary: blue,
          secondary: pink
        }
      });

    const customTheme = createMuiTheme({
        palette: {
          primary: purple,
          secondary: pink
        }
      });
      const classes = useStyles();
    return(

        <div>

        <form  className={classes.root}> 
          <div className='row'>
            <div className='col-md-6'>

             <label htmlFor='name'>Name *</label> 

              <TextField
          required
          id="outlined-required"
          label="Required"
          type="text"
          placeholder="ENTER YOUR NAME"
          name="Name"
        value={name}
        onChange={handleChange}


        />            </div>


          </div>

          <br/>

          <div className='mb-3'>
            <label htmlFor='email'>Email *</label>
            <TextField

          required

          id="outlined-required"
          label="Required"
          placeholder="ENTER YOUR EMAIL"
          name= "email"
          value={email}
          onChange={handleChange}





        />

          </div>
          < br/>
          <div className='mb-3'>
            <label htmlFor='password'>Password</label>


            <TextField
          id="outlined-password-input"
          label="Password"
          type="password"
          autoComplete="current-password"
          name = "password"
          value={password}
          onChange={handleChange}



        />

          <br/>
          <div className ="App">
        <input type ="file" onChange={(e)=>{
            uploadImage(e);

        }}/>
    </div>


          <ThemeProvider theme={customTheme}>





          <div>

        <Button type="submit" variant="contained" color="primary" onClick={Submit}

        >
          Signup
        </Button>
      </div>





           </ThemeProvider> 
          </div>




        </form>
      </div>

    )
}
export default SignUp;
