const saveData=(data,key)=>{
    const user = localStorage.getItem(key,data)
    console.log(user);
    const userdetail = {
        name:data.name,
        email:data.email,
        password:data.password
    }
    console.log(userdetail);

    if(user==null){
        const arr=[];
        arr.push(userdetail);
        localStorage.setItem('user',JSON.stringify(arr));
    }
}

export default saveData;
