import React, { Component, useState } from "react";


const Login=()=>{
    const [email,setemail]=useState('');
    const [password,setpassword]=useState('');
    const [entry,setnewentry]=useState([]);

    const  SubmitForm =()=>{
        const newEntry={email:email,password:password}
        setnewentry ([...entry,newEntry])
        alert('user login in successfully')

    }

    return (
        <form action =" " onSubmit={SubmitForm}>
            <h3>Sign In</h3>

            <div className="form-group">
                <label>Email address</label>
                <input type="email" className="form-control" placeholder="Enter email"  value={email}
                onChange={(e)=>{
                    setemail((e.target.value))
                }}/>
            </div>
            <br/>

            <div className="form-group">
                <label>Password</label>
                <input type="password" className="form-control" placeholder="Enter password" value={password}
                onChange={(e)=>{
                    setpassword((e.target.value))
                }}/>
            </div>

            <button type="submit" className="btn btn-primary btn-block">Submit</button>

            <div>{
                 entry.map((elements)=>{
                     return(
                         <div>
                      <p>  {elements.email}</p>
                      <p>{elements.password}</p>
                      </div>

                     )
                     })
                }

            </div>

        </form>
    );


}



export default Login;
