import React from "react";
import { Formik, Form, Field } from "formik";
import { Button, Grid } from "@material-ui/core";
import{createSchema} from'./validations';
import { useHistory } from "react-router";
import Lazy from "yup/lib/Lazy";
import{styled} from "@material-ui/styles";
const MyButton = styled(Button)({
  background: 'blue',
  border: 0,
  borderRadius: 50,
  color: 'white',
  height: 50,
  padding: '0 40px',
  margin:40
});

const FormComponent=(props)=>{
  const history= useHistory();
  const login=()=>{
    history.push('login');


  }

const schema = createSchema(props.fields);

   const renderField = (props) => {



        return (
          <Grid item xs={props.grid || 12}>
            <Field key={props.name} {...props} />
          </Grid>
        );
      };

  const renderForm = ({ handleSubmit }) => {
        const { fields, renderCustomSubmit, btn ,btn1} = props;
        return (
          // onSubmit={handleSubmit}
          <div className="form">



<Form>

            <>
            <div className="Container">
           <div className="ltext">
           <label htmlFor="Sign up">Sign up</label>
           </div>
              <Grid container spacing={2}>
                {fields.map(renderField)}
              </Grid>
              </div>
              <div className="btns">


              {renderCustomSubmit || (
                <MyButton  color ="primary" variant="contained" type="submit" size="large">
                  {btn}
                </MyButton>
              )}
              <label htmlFor="Sign up">Already have an account ?</label>


              </div>
              {/* <Button  variant ="contained" onClick={login}>{btn1}</Button> */}


            </>
          </Form>
          </div>
        );
      };





      return (

        <Formik
          initialValues={props.initialValues}
          onSubmit={props.onSubmit}
          validationSchema={schema}
        >
          {renderForm}
        </Formik>

    );


}
export default FormComponent;
