   getData(data,key);

}
const history =useHistory();
const getData=(data,key)=>{


    const loginuser = localStorage.getItem(key,data);
    const info = {
        name:data.password,
        email:data.email,
    }
    console.log(info);
    const d = JSON.parse(loginuser);
    if(d.some(o => o.email == data.email && o.password==data.password)){
      alert('User Login');

      history.push('/');
    }
    else{
        alert('Signup Please!');
    }

}

export default saveData;
