        name:data.password,
        email:data.email,
    }
    console.log(info);
    const d = JSON.parse(loginuser);
    if(d.some(o => o.email == data.email && o.password==data.password)){
      alert('User Login');
     history.push('dashboard');
    }
    else{
        alert('Signup Please!');
    }

}

export default saveData;
